package del_16_al_20;

public class Ejercicio17 {

	/**
	 * Escribe por pantalla los números del 1 al 100.
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 0 ; i < 100 ; i++){ //Estructura For: exige que se le inicialice una variable local (iterador), una condición de salida y el incremento o decremento
			System.out.println((i+1)); //El iterador internamente va de 0 a 99; para que muestre i+1 como una operación y no como texto debe ir entre paréntesis
		}
	}

}
