package del_16_al_20;

public class Ejercicio20 {

	/**
	 * Escribe los 20 primeros números pares empezando por 2 inclusive.
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//En este caso, la similitud con el Ejercicio 19 es muy alta, pero es más apropiado usar este tipo de estructura 
		for(int i = 1 ; i <= 20; i++){
			System.out.println((i*2));
		}
	}

}
